class i{accordion;header;content;constructor(t){this.accordion=document.querySelector(`.${t}`),this.header=this.accordion.querySelector(".accordion__header"),this.content=this.accordion.querySelector(".accordion__content"),this.activateAccordion()}activateAccordion(){this.header.addEventListener("click",()=>this.playAccordion())}playAccordion(){this.accordion.classList.toggle("accordion_active"),this.content.style.maxHeight!=""?this.content.style.maxHeight="":this.content.style.maxHeight=this.content.scrollHeight+"px"}}class s{allAccordions;constructor(t){this.allAccordions=document.querySelectorAll(`.${t}`),this.addListener()}addListener(){this.allAccordions.forEach(t=>{const o=t.querySelector(".accordion__header"),e=t.querySelector(".accordion__content");o.addEventListener("click",()=>{t.classList.toggle("accordion_active"),e.style.maxHeight!=""?e.style.maxHeight="":e.style.maxHeight=e.scrollHeight+"px"})})}}export{i as A,s as a};
